exports['reduce-all'] = function(event,operation) {
    operation.cutStart = 0;
    operation.cutEnd = operation.text.length;
    var zwrds = operation.text.toLowerCase().replace(	/\W/g,' ');
    var ztrc = zwrds.split(' ').sort().reverse();
    var zntrc = ' ';
    var add = '';
    for (var i = 0; i < ztrc.length; i++) {
        if (zntrc.indexOf(ztrc[i]) < 1) {
            add = ztrc[i].toString();
            zntrc += add;
        }
    }
    var t= zntrc.slice(1);
    operation.replacement = zntrc.slice(1);
    operation.newSelStart = 0;
    operation.newSelEnd = operation.replacement.length;
};

