title: $:/plugins/ajh/tiddlytime/datetime.js
type: application/javascript
module-type: macro

<<dt format delay>>

Examples:
<<dt>>
<<dt "YYYY-0MM-0DD 0hh:0mm:0ss">>
<<dt "YYYY-0MM-0DD 0hh:0mm:0ss" "30">>
<$macrocall $name="dt" ts={{!!created}} format="MMM DD. YY"/>

Format Strings:
Please find all format strings listed in the JavaScript code of function formatDateString in $:/core/modules/utils/utils.js
\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

/*
This is a macro to get formatted date and/or time plus delay in minutes
*/

exports.name = "dt";

exports.params = [
	{ name: "format" },
	{ name: "delay" },
	{ name: "ts" }
];

/*
Run the macro
*/
exports.run = function(format, delay, ts) {
	if(!ts) {
		ts = new Date;
		ts.setMinutes(ts.getMinutes() + Number(delay));
	}
	else {
		ts = $tw.utils.parseDate(ts);
	}
	if(!format)
		format = "hh12:0mm:0ss AM";
	return $tw.utils.formatDateString(ts, format); 
};

})();","type":"application/javascript","title":"$:/plugins/ajh/tiddlytime/datetime.js","module-type":"macro"},"$:/plugins/ajh/tiddlytime/icon":{"text":"<svg class='tc-icon-tiddlytime'viewBox='0 0 128 128'><path class='tc-badge-tiddlytime'd='m12.5,33l51.2,-29.44l51.2,29.44v61.44l-51.2,29.44l-51.2,-29.44v-61.44z'/><path d='m19,64a45,45,90,1,0,90,0a45,45,90,1,0-90,0zm44,-37v38l16,26l-16,-26v-38zm-38,38h7zm38,38v-7zm31,-38h7z'/></svg>","title":"$:/plugins/ajh/tiddlytime/icon","tags":"$:/tags/Image"},"$:/plugins/ajh/tiddlytime/license":{"text":"<a href={{$:/plugins/ajh/tiddlytime!!source}}>Tiddlytime Button</a> Copyright (c) 2019-<<now YYYY>> Andrew Harrison

